package com.example.minispotify.dao

import androidx.room.*
import com.example.minispotify.objetos.PlaylistEntity

@Dao
interface PlaylistDao {
    @Query("SELECT * FROM Playlist")
    fun getAllPlaylists(): MutableList<PlaylistEntity>
    @Query("INSERT INTO Playlist(titulo,imagen)" +
            "VALUES" +
            "('Thumbnail','https://cdn.dribbble.com/users/5783048/screenshots/13902636/skull_doodle_4x.jpg')," +
            "('Chill','https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRVWF0nUxYDxuT0RH8DNA7r_ki4NNURWfrv4Q&usqp=CAU')," +
            "('Hot','data:image/jpeg;base64,')," +
            "('Gym','https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQhf-8xPk-wKLoU-1wSZHdXhb7sn-spbrVMUrS3ER30q8kwxC4ijE244qiwZ-RToIXSodQ&usqp=CAU')," +
            "('Predetermined','https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRl3_X9fflbbxO5Iw12aml6VcKfNtUGbO_IMJ-iURd_Dh3vpvSRj6xXmZMNenilt077Iew&usqp=CAU');")
    fun playlistPre()
    @Insert
    fun addPlaylist(playlistEntity: PlaylistEntity)
    @Update
    fun updatePlaylist(playlistEntity: PlaylistEntity)
    @Delete
    fun deletePlaylist(playlistEntity: PlaylistEntity)

}